mapper.select:
SELECT a.seq ,a.name ,a.insertDate ,a.updateDate ,a.delNy
FROM codegroup a WHERE 1=1 and a.delNy = #{shDelNy}
AND a.insertDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.updateDate BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.seq = #{shValue} AND a.name LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.seq DESC

mapper.update:
UPDATE codeGroup
set name = #{name}
where seq = #{seq}

UPDATE codeGroup
set delYn = 1
where seq = #{seq}
